request.query:
    {
    ticket(id: "0x62bb4fc73094c83b5e952c2180b23fa7054954c4") {
        accounts(first: 1000 , where: { 

            id_gt: "0x4f686daa6a2f2b5412a95fc7da3cb6e041cb412a"
            }) {
            id
            delegateBalance


            # get twab beforeOrAt drawStartTime
            beforeOrAtDrawStartTime: twabs(
                orderBy: timestamp
                orderDirection: desc
                first: 1
                where: { timestamp_lte: 1706900975 } #drawStartTime
            ) {
                amount
                timestamp
                delegateBalance
            }
            # now get twab beforeOrAt drawEndTime (may be the same as above)
            beforeOrAtDrawEndTime: twabs(
                orderBy: timestamp
                orderDirection: desc
                first: 1
                where: { timestamp_lte: 1706986475 } #drawEndTime
            ) {
                amount
                timestamp
                delegateBalance
            }
        }
    }
}
